const booksLoaded = (payload) => ({type: 'BOOKS_LOADED', payload});
const booksRequested = _ => ({type: 'BOOKS_REQUESTED'});
const booksError = (payload) => ({type: 'BOOKS_ERROR', payload});
export const fetchBooks = (BookStoreService, dispatch) => () => {
    dispatch(booksRequested()); // показать спиннер
    BookStoreService.getBooks().then(data => dispatch(booksLoaded(data))).catch(error => dispatch(booksError(error)))
}
