import React, {Component} from 'react';
import BookListItem from "../BookListItem/BookListItem";
import {connect} from "react-redux";
import {WithBookStoreService} from "../HOC/WithBookStoreService";
import {booksLoaded} from "../../actions";
import {compose} from "../../utils/compose";

class BookList extends Component {
    componentDidMount() {
        const {BookStoreService:{getBooks}, booksLoaded} = this.props
        const data = getBooks()
        booksLoaded(data)
    }

    render() {
        const {books} = this.props
        return (
            <ul className=''>
                {books.map(book=><li key={book.id}><BookListItem book={book}/></li>)}
            </ul>
        );
    }
}

const mapStateToProps = ({books}) => ({
    books
})
//export const booksLoaded = (payload) => ({type:'BOOKS_LOADED', payload})
const mapDispatchToProps = {
    booksLoaded
}

export default compose(WithBookStoreService(), connect(mapStateToProps, mapDispatchToProps))(BookList);
