const initialState = {
    books: [],
    loading: true,
    error: null,
    cartItems: [],
    orderTotal: 0
}

export const reducer = (state = initialState, action) => {


    switch (action.type) {

        case 'FETCH_BOOKS_SUCCESS' :
            return {
                ...state,
                books: action.payload,
                loading: false,
                error: null
            }

        case 'FETCH_BOOKS_REQUEST' :
            return {
                ...state,
                books: [],
                loading: true,
                error: null
            }

        case 'FETCH_BOOKS_FAILURE' :
            return {
                ...state,
                books: [],
                loading: false,
                error: action.payload
            }

        case 'BOOK_ADDED_TO_CART' :
            const ID = action.payload;

            const isBookInCart = state.cartItems.find(book=>book.id === ID)
            if (isBookInCart) {
                const newItem = {...isBookInCart}
                newItem.total = newItem.total / newItem.count * 2
                newItem.count = ++newItem.count
                const newList = state.cartItems.filter(book=>book.id !== ID)
                return {
                    ...state,
                    cartItems: [...state.cartItems, data],
                    orderTotal: state.orderTotal + data.total
                }
            }
            const currentBook = state.books.find(book=>book.id === ID)
            const data = {
                id: currentBook.id,
                name: currentBook.name,
                count: 1,
                total: currentBook.price,
            }
            return {
                ...state,
                cartItems: [...state.cartItems, data],
                orderTotal: state.orderTotal + data.total
            }

        default:
            return state
    }
}
