import {bindActionCreators, createStore} from "redux";
import * as actionCreators from '../src/actionCreators'


const initialState = 0

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'ADD' :
            return state + 1
        case 'GET' :
            return state - 1
        default:
            return state
    }
}





const store = createStore(reducer)

const {ADD, GET} = bindActionCreators(actionCreators ,store.dispatch)


document.querySelector('#add').addEventListener('click', ()=>store.dispatch(ADD()) )
document.querySelector('#get').addEventListener('click', ()=>store.dispatch(GET()) )
document.querySelector('#counter').innerHTML = store.getState()

const updateH2 = () => {
    document.querySelector('#counter').innerHTML = store.getState()
}

store.subscribe(updateH2)
