const initialState = {
    books: [],
    loading: true,
    error: null,
    cartItems: [],
    orderTotal: 0,
    cartItemsTotal: 0
}

const updateCartItems = (cartItems, item, index) => {
    if (index === -1) {
        return [
            ...cartItems,
            item
        ]
    }
    return [
        ...cartItems.slice(0, index),
        item,
        ...cartItems.slice(++index)
    ]
}

const updateCartItem = (book, item = {}) => {
    const {id = book.id, name = book.name, count = 0, total = 0} = item
    return {
        id,
        name,
        count: count + 1,
        total: book.price + total,
    }
}

const updateOrderTotal = (cartItems) => {
    console.log(typeof cartItems)
    if (typeof cartItems === 'object' ) return 1
    return cartItems.reduce((acc, el) => acc + el.total, 0)
}

const updateCartItemsTotal = (cartItems) => {
    return cartItems.reduce((acc, el) => acc + el.count, 0)
}

export const reducer = (state = initialState, action) => {

    switch (action.type) {

        case 'FETCH_BOOKS_SUCCESS' :
            return {
                ...state,
                books: action.payload,
                loading: false,
                error: null
            }

        case 'FETCH_BOOKS_REQUEST' :
            return {
                ...state,
                books: [],
                loading: true,
                error: null
            }

        case 'FETCH_BOOKS_FAILURE' :
            return {
                ...state,
                books: [],
                loading: false,
                error: action.payload
            }

        case 'BOOK_ADDED_TO_CART' :
            const ID = action.payload; // clicked id
            const currentBook = state.books.find(book => book.id === ID) // book from list
            const isBookInCartIndex = state.cartItems.findIndex(book => book.id === ID) // >0 || -1
            const bookInCart = state.cartItems[isBookInCartIndex] // index || undefined
            const data = updateCartItem(currentBook, bookInCart)
            return {
                ...state,
                cartItems: data,
                orderTotal: updateOrderTotal(data)
            }

        case 'BOOKS_DELETED_FROM_CART' :
            const booksID = action.payload; // clicked id
            const priceOfDeletedBooks = state.cartItems.find(({id}) => id === booksID).total;
            const newCartList = state.cartItems.filter(({id}) => id !== booksID);
            return {
                ...state,
                cartItems: newCartList,
                orderTotal: updateOrderTotal(newCartList)
            }

        case 'BOOK_DELETED_FROM_CART' :
            const bookID = action.payload; // clicked id
            const newCarts = [...state.cartItems]
            const deletedBooksGroup = newCarts.find(({id}) => id === bookID)
            const getCount = deletedBooksGroup.count - 1
            if (getCount === 0) {
                return {
                    ...state,
                    cartItems: newCarts.filter(({id}) => id !== bookID)
                }
            }
            const getTotal = deletedBooksGroup.total / deletedBooksGroup.count
            deletedBooksGroup.count = getCount
            deletedBooksGroup.total -= getTotal
            return {
                ...state,
                cartItems: newCarts
            }
        default:
            return state
    }
}
