const initialState = {
    books: [],
    loading: true,
    error: null
}

export const reducer = (state = initialState, action) => {
    switch (action.type) {

        case 'BOOKS_LOADED' :
            return {
                ...state,
                books: action.payload,
                loading: false
            }

        case 'BOOKS_REQUESTED' :
            return {
                ...state,
                books: [],
                loading: true
            }

        case 'BOOKS_ERROR' :
            return {
                ...state,
                books: [],
                loading: false
            }

        default:
            return state
    }
}
