import React, {Component} from 'react';
import './ShoppingCardTable.css'

class ShoppingCardTable extends Component {
    render() {
        const {items, total, onAdd, onGet, onDel} = this.props
        return (
            <div className='shopping-cart-table'>
                <h2>Your Order</h2>
                <table className='table'>
                    <thead>
                    <tr>
                        <th>#</th>
                        <th>Item</th>
                        <th>Count</th>
                        <th>Price</th>
                        <th>Action</th>
                    </tr>
                    </thead>
                    <tbody>
                    {items.map((item, index)=><TableItem
                        name={item.name}
                        count={item.count}
                        id={item.id}
                        add={onAdd}
                        idx={index}
                        dell={onDel}
                        get={onGet}
                        total={total}
                    />)}
                    <TableItem name={'AAA'} count={40} id={1} add={console.log} idx={2} dell={()=>{}} get={()=>{}} total={200}/>
                    </tbody>
                </table>
                <div className='total'>
                    Total: 201$
                </div>
            </div>
        );
    }
}

const TableItem = ({id, idx, name, count, total, add, dell, get}) => <tr>
    <td>{idx}</td>
    <td>{name}</td>
    <td>{count}</td>
    <td>{total}$</td>
    <td>
        <button onClick={()=>{get(id)}} className='btn btn-outline-danger btn-sm float-right'><i className='fa fa-trash-o'/></button>
        <button onClick={()=>{add(id)}} className='btn btn-outline-success btn-sm float-right'><i className='fa fa-plus-circle'/></button>
        <button onClick={()=>{dell(id)}} className='btn btn-outline-warning btn-sm float-right'><i className='fa fa-minus-circle'/></button>
    </td>
</tr>

export default ShoppingCardTable;
