const initialState = {
    books: [],
    loading: true,
    error: null,
    cartItems: [],
    orderTotal: 0
}

const updateCartItems = (cartItems, item, index) => {
    if (index === -1) {
        return [
            ...cartItems,
            item
        ]
    }
    return [
        ...cartItems.slice(0, index),
        item,
        ...cartItems.slice(++index)
    ]
}

const updateCartItem = (book, item = {}) => {
    const {id = book.id, name = book.name, count = 0, total = 0} = item
    return {
        id,
        name,
        count: count + 1,
        total: book.price + total,
    }
}

export const reducer = (state = initialState, action) => {

    switch (action.type) {

        case 'FETCH_BOOKS_SUCCESS' :
            return {
                ...state,
                books: action.payload,
                loading: false,
                error: null
            }

        case 'FETCH_BOOKS_REQUEST' :
            return {
                ...state,
                books: [],
                loading: true,
                error: null
            }

        case 'FETCH_BOOKS_FAILURE' :
            return {
                ...state,
                books: [],
                loading: false,
                error: action.payload
            }

        case 'BOOK_ADDED_TO_CART' :
            const ID = action.payload; // clicked id
            const currentBook = state.books.find(book => book.id === ID) // book from list
            const isBookInCartIndex = state.cartItems.findIndex(book => book.id === ID) // >0 || -1
            const bookInCart = state.cartItems[isBookInCartIndex] // index || undefined
            const data = updateCartItem(currentBook,)
            return {
                ...state,
                cartItems: updateCartItems(state.cartItems, data, isBookInCartIndex),
                orderTotal: state.orderTotal + currentBook.price
            }


        default:
            return state
    }
}
