const initialState = {
    books: [],
    loading: true,
    error: null,
    cartItems: [
        {
            id: 1,
            name: 'Book 228',
            count: 3,
            total: 158,

        },
        {
            id: 2,
            name: 'Book 555',
            count: 8,
            total: 900,

        },
    ],
    orderTotal: 1058
}

export const reducer = (state = initialState, action) => {


    switch (action.type) {

        case 'FETCH_BOOKS_SUCCESS' :
            return {
                ...state,
                books: action.payload,
                loading: false,
                error: null
            }

        case 'FETCH_BOOKS_REQUEST' :
            return {
                ...state,
                books: [],
                loading: true,
                error: null
            }

        case 'FETCH_BOOKS_FAILURE' :
            return {
                ...state,
                books: [],
                loading: false,
                error: action.payload
            }

        case 'BOOK_ADDED_TO_CART' :
            return {
                ...state,
                cartItems: [...state.cartItems,]
            }

        default:
            return state
    }
}
