import {createStore} from "redux";


const initialState = 0

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'ADD' :
            return state + 1
        case 'GET' :
            return state - 1
        default:
            return state
    }
}
const GET = () => {
    return {
        type: 'GET'
    }
}
const ADD = () => {
    return {
        type: 'ADD'
    }
}


const store = createStore(reducer)


document.querySelector('.add').addEventListener('click', store.dispatch(ADD))
document.querySelector('.get')
document.querySelector('.counter')
