const initialState = 0

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'ADD' : return state + 1
        default: return state
    }
}

let state = reducer(initialState, {type: 'ADD'})
state = reducer(state, {type: 'ADD'})
state = reducer(state, {type: 'ADD'})
console.log(state)
