import React, {Component} from 'react';
import BookListItem from "../BookListItem/BookListItem";
import {connect} from "react-redux";
import {WithBookStoreService} from "../HOC/WithBookStoreService";
import {fetchBooks} from "../../actions";
import {compose} from "../../utils/compose";
import './BookList.css'
import Spinner from "../Spinner/Spinner";
import ErrorComponent from "../ErrorComponent/ErrorComponent";

class BookListContainer extends Component {
    componentDidMount() {
        const {fetchBooks} = this.props
        fetchBooks()
    }


    render() {
        const {books, loading, error} = this.props
        if (error) return <ErrorComponent message={error}/>
        return loading && books.length === 0 ?
            <Spinner/>
            :
            <ul className='book-list'>
                {books.map(book => <li key={book.id}><BookListItem book={book}/></li>)}
            </ul>
            ;
    }
}

const mapStateToProps = ({books, loading, error}) => ({
    books, loading, error
})
//export const booksLoaded = (payload) => ({type:'BOOKS_LOADED', payload})

/* V1
const mapDispatchToProps = {
    booksLoaded,
    booksRequested,
    booksError
}
 */

/* V2
const mapDispatchToProps = (dispatch, ownProps) => ({ // эта функция в 2 параметр принимает все пропсы переданные из hoc ранее а также из компонент выше
    fetchBooks: () => {
        const {BookStoreService: {getBooks}} = ownProps
        dispatch(booksRequested()); // показать спиннер
        getBooks().then(data => dispatch(booksLoaded(data))).catch(error => dispatch(booksError(error)))
    }
})
 */
const mapDispatchToProps = (dispatch, ownProps) => ({ // эта функция в 2 параметр принимает все пропсы переданные из hoc ранее а также из компонент выше
    fetchBooks: fetchBooks(ownProps.BookStoreService, dispatch)
})

export default compose(WithBookStoreService(), connect(mapStateToProps, mapDispatchToProps))(BookListContainer);
