const initialState = {
    books: [],
    loading: true
}

export const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'BOOKS_LOADED' :
            return {
                ...state,
                books: action.payload,

            }

        default:
            return state
    }
}
