const initialState = {
    books: [
        {id:1, name: 'Сказки', author: 'Alex Hitch'},
        {id:2, name: 'Комиксы', author: 'Boris Godunov'},
        {id:3, name: 'Анекдоты', author: 'Mary Strict'},
    ],
}

export const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'BOOKS_LOADED' :
            return {
                ...state,
                books: action.payload
            }

        default:
            return state
    }
}
