import {createStore} from "redux";


const initialState = 0

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'ADD' :
            return state + 1
        default:
            return state
    }
}

const unSub = () => {}

const store = createStore(reducer)
store.subscribe(()=>{
    console.log('store was edited')
return})
store.dispatch({type:'ADD'})
store.dispatch({type:'ADD'})
