export const reducer = (state = initialState, action) => {

    switch (action.type) {

        case 'FETCH_BOOKS_SUCCESS' :
            return {
                ...state,
                books: action.payload,
                loading: false,
                error: null
            }

        case 'FETCH_BOOKS_REQUEST' :
            return {
                ...state,
                books: [],
                loading: true,
                error: null
            }

        case 'FETCH_BOOKS_FAILURE' :
            return {
                ...state,
                books: [],
                loading: false,
                error: action.payload
            }

        case 'BOOK_ADDED_TO_CART' :
            const ID = action.payload; // clicked id
            const currentBook = state.books.find(book => book.id === ID) // book from list
            const isBookInCartIndex = state.cartItems.findIndex(book => book.id === ID) // >0 || -1
            const bookInCart = state.cartItems[isBookInCartIndex] // index || undefined
            let data;
            if (bookInCart) {
                data = {
                    ...bookInCart,
                    count: bookInCart.count + 1,
                    total: bookInCart.total + (bookInCart.total / bookInCart.count),
                }
            } else {
                data = {
                    id: currentBook.id,
                    name: currentBook.name,
                    count: 1,
                    total: currentBook.price,
                }
            }

            if (isBookInCartIndex < 0) {
                return {
                    ...state,
                    cartItems: [...state.cartItems, data],
                    orderTotal: state.orderTotal + currentBook.price
                }
            } else {
                return {
                    ...state,
                    cartItems: [...state.cartItems.slice(0, isBookInCartIndex), data, ...state.cartItems.slice(isBookInCartIndex + 1)],
                    orderTotal: state.orderTotal + currentBook.price
                }
            }


        default:
            return state
    }
}
