import React, {Component} from 'react';
import BookListItem from "../BookListItem/BookListItem";
import {connect} from "react-redux";
import {WithBookStoreService} from "../HOC/WithBookStoreService";
import {booksError, booksLoaded, booksRequested} from "../../actions";
import {compose} from "../../utils/compose";
import './BookList.css'
import Spinner from "../Spinner/Spinner";
import ErrorComponent from "../ErrorComponent/ErrorComponent";

class BookList extends Component {
    componentDidMount() {
        const {BookStoreService:{getBooks}, booksLoaded, booksError} = this.props
        getBooks().then(data=>booksLoaded(data)).catch(error=>booksError(error))
    }
    componentWillUnmount() {
        const {booksRequested} = this.props
        booksRequested()
    }

    render() {
        const {books, loading, error} = this.props
        if (error) return <ErrorComponent/>
        return loading && books.length === 0 ?
            <Spinner/>
            :
            <ul className='book-list'>
                {books.map(book=><li key={book.id}><BookListItem book={book}/></li>)}
            </ul>
        ;
    }
}

const mapStateToProps = ({books, loading, error}) => ({
    books, loading, error
})
//export const booksLoaded = (payload) => ({type:'BOOKS_LOADED', payload})
const mapDispatchToProps = {
    booksLoaded,
    booksRequested,
    booksError
}

export default compose(WithBookStoreService(), connect(mapStateToProps, mapDispatchToProps))(BookList);
